n:big-image 1.4s ease}@-webkit-keyframes big-image{from{-webkit-transform:translateY(60%);transform:translateY(60%);opacity:.5}}@keyframes big-image{from{-webkit-transform:translateY(60%);transform:translateY(60%);opacity:.5}}.big-banner .big-badge{position:absolute;top:0;right:0;background:#000;padding:.35rem;text-transform:uppercase;font-size:x-small;letter-spacing:1px;color:rgba(255,255,255,.7);z-index:3}@media (max-width:768px){.big-banner{height:200px}.big-banner .big-content{padding:17px 37px 0;max-width:71%}.big-banner .big-content h1{font-size:min(3.8vmin,40px)}.big-banner .big-content h2{font-size:max(2.2vmin,18px)}.big-banner .big-content a{font-size:1rem}}@media (max-width:568px){.big-banner{height:280px}.big-banner .big-content{padding:17px 37px 0;max-width:71%}.big-banner .big-content h1{font-size:min(6vmin,40px)}.big-banner .big-content h2{font-size:min(4.5vmin,18px)}.big-banner .big-content a{font-size:small}.big-banner .big-img{width:59%}}
